import React from 'react';
import styles from './Button.module.scss';

const Button = ({
    children,
    href,
    secondary,
}) => (
    <>
    {
        href ? (
            <a 
                href={href}
                target="_blank" 
                rel="noopener noreferrer"
                className={styles.button}
            >
                {children}
            </a>
        ) : (
            <button  className={styles.button}>
                {children}
            </button>
        )
    }  
    </>
);

export default Button;
