import React from 'react';
import Input from '../Input/Input';
import Button from '../Button/Button';
import Title from '../Title/Title';
import Radio from './Radio/Radio';
import styles from './Form.module.scss';

const types = {
    twitter: 'twitter',
    article: 'article',
    note: 'note',
}

const descriptions = {
    twitter: 'Fav Twitter',
    article: 'Article',
    note: 'Note',
}

class Form extends React.Component {
    state = {
        activeOption: types.twitter,
    };

    handeRadioButtonChange = (type) => {
        this.setState({
            activeOption: type,
        })
    };

    render() {
        return (
            <div className={styles.wrapper}>
            <Title>Add new {descriptions[this.state.activeOption]}</Title>
            <form 
                autoComplete="off" 
                className={styles.form} 
                onSubmit={this.props.submitFn}
            >
                <div className={styles.formOptions}>



                </div>


                <Input 
                    name="name"
                    label="Title"
                    maxLength={30}
                />
                 <Input 
                    name="imdbLink"
                    label="Imdb link"
                />
                 <Input 
                    name="image"
                    label="Image"
                />
                 <Input 
                    tag="textarea"
                    name="description"
                    label="Description"
                />

                <Button>Add new position</Button>
            </form>
        </div>
        )
    }
};

export default Form;
